def _create_nested_container_client(blob_service_client: BlobServiceClient, container_name: str, subfolder: str):
    new_name_for_container = container_name + "/" + subfolder[0:6]
    return blob_service_client.get_container_client(container=new_name_for_container)

def upload_blob_file_1979_2005(blob_service_client: BlobServiceClient, container_name):
    container_client = blob_service_client.get_container_client(container=container_name)

    for dt in range(1979,2006,1):
        dt_str = str(dt)+'.zip'
        send_data_to_blob(container_client,dt_str)

def upload_blob_file_200601_201303(blob_service_client: BlobServiceClient, container_name: str):

    for dt in pd.period_range(start='2006-01-01', end='2013-03-01', freq='M'):
