def upload_blob_file_for_dates(blob_service_client: BlobServiceClient, container_name: str, date_start:str, date_end:str):
    '''date_start and date_end in format YYYY-MM-DD'''

    for dt in pd.period_range(start=date_start, end=date_end, freq='D'):
        dt_str = str(dt).replace("-","")+'.export.CSV.zip'

        container_client = _create_nested_container_client(blob_service_client, container_name, dt_str)

        send_data_to_blob(container_client,dt_str)
